   content: "";
   grid-area: 1/1;
 }
 .loader,
 .loader::before,
 .loader::after{
   height: var(--s);
   width: calc(var(--s) / 5);
   border-radius: var(--s);
   background: var(--c) calc(50% + var(--_i, 0) * 50%) / var(--s) var(--s);
   transform: translate(calc(var(--_i, 0) * 200%));
 }
